css:
.markdown-writer-dialog .dialog-row {
  margin-bottom: 10px;
}
.markdown-writer-dialog .side-label {
  margin-left: 10px;
}
.markdown-writer-dialog .image-container {
  max-height: 320px;
  overflow: scroll;
}
.markdown-writer-dialog .image-container img {
  max-width: 100%;
  margin: 0 auto;
}
.markdown-writer-dialog .col-1 {
  display: inline-block;
  width: 31%;
  margin-right: 2%;
}
.markdown-writer-dialog .col-2 {
  display: inline-block;
  width: 34%;
}
.markdown-writer-list {
  list-style: none;
  padding: 0;
  margin: 0;
  max-height: 170px;
  overflow: scroll;
}
.markdown-writer-list li {
  padding: 5px 3px;
  border: 1px solid rgba(0, 0, 0, 0.15);
  background-color: #f4f4f4;
  margin-bottom: 2px;
  cursor: pointer;
}
.markdown-writer-list li:hover {
  background-color: #ffffff;
  color: #000;
}
.markdown-writer-selection .candidates {
  list-style: none;
  padding: 0;
  height: 209px;
  overflow-y: scroll;
}
.markdown-writer-selection .candidates li {
  display: inline-block;
  padding: 5px;
  margin: 0 2px 2px 0;
  border: 1px solid rgba(0, 0, 0, 0.15);
  background-color: #f4f4f4;
  cursor: pointer;
}
.markdown-writer-selection .candidates li.selected {
  background-color: #888;
  color: #fff;
}
.markdown-writer-selection .candidates li.selected:hover {
  background-color: #17ca65;
  color: #000;
}
.markdown-writer-selection .candidates li:hover {
  background-color: #ffffff;
  color: #000;
}
