css:
.github-IssueishDetailView {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-color: #ffffff;
}
.github-IssueishDetailView-container {
  display: flex;
  flex-direction: column;
  height: 100%;
}
.github-IssueishDetailView .react-tabs {
  flex: 1;
  display: flex;
  flex-direction: column;
}
.github-IssueishDetailView .react-tabs__tab-panel--selected {
  flex: 1;
  overflow: auto;
}
.github-IssueishDetailView-header {
  display: flex;
  align-items: center;
  padding: 15px 20px;
  border-bottom: 1px solid #e0e0e0;
}
.github-IssueishDetailView-headerColumn.is-flexible {
  flex: 1;
  display: flex;
  flex-wrap: wrap;
}
.github-IssueishDetailView-headerRow {
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  margin: 2px 0;
}
.github-IssueishDetailView-headerRow.is-fullwidth {
  flex: 1 1 100%;
}
.github-IssueishDetailView-avatar {
  align-self: flex-start;
  margin-right: 10px;
}
.github-IssueishDetailView-title {
  margin: 0;
  font-size: 1.25em;
  font-weight: 500;
  line-height: 1.3;
  color: #292929;
}
.github-IssueishDetailView-title:hover,
.github-IssueishDetailView-title:focus {
  text-decoration: none;
}
.github-IssueishDetailView-headerBadge {
  margin-right: 10px;
  padding: 0 0.75em;
  line-height: 1.65em;
}
.github-IssueishDetailView-headerLink {
  color: #949494;
}
.github-IssueishDetailView-headerStatus {
  margin-left: 10px;
}
.github-IssueishDetailView-checkoutButton {
  font-weight: 600;
  margin-left: 10px;
}
.github-IssueishDetailView-headerRefreshButton {
  display: inline-block;
  margin-right: 10px;
  color: #949494;
  cursor: pointer;
}
.github-IssueishDetailView-headerRefreshButton::before {
  display: block;
  width: 16px;
  height: 16px;
  line-height: .9;
  margin-right: 0;
  text-align: center;
}
.github-IssueishDetailView-headerRefreshButton.refreshing::before {
  animation: github-IssueishDetailView-headerRefreshButtonAnimation 2s linear 30;
}
@keyframes github-IssueishDetailView-headerRefreshButtonAnimation {
  100% {
    transform: rotate(360deg);
  }
}
.github-IssueishDetailView-avatarImage {
  width: 40px;
  height: 40px;
  border-radius: 3px;
}
.github-IssueishDetailView-meta {
  color: #949494;
}
.github-IssueishDetailView-metaAuthor {
  color: inherit;
}
.github-IssueishDetailView-tablist {
  flex: none;
  display: flex;
  justify-content: center;
  padding: 0;
  margin: 0;
  list-style: none;
  border-bottom: 1px solid #e0e0e0;
  background-color: #f0f0f0;
}
.github-IssueishDetailView-tab {
  padding: 5px 15px;
  text-align: center;
  font-weight: 600;
  color: #717171;
  cursor: default;
  user-select: none;
}
.github-IssueishDetailView-tab-icon {
  color: #b8b8b8;
  vertical-align: middle;
}
.github-IssueishDetailView-tab-icon:before {
  margin-right: .4em;
}
.github-IssueishDetailView-tab-count {
  display: inline-block;
  background-color: #dddddd;
  border-radius: 3px;
  padding: 0 .25em;
  margin-left: 5px;
}
.github-IssueishDetailView-tab.react-tabs__tab--selected {
  color: #0a0a0a;
  box-shadow: 0 1px 0 #5a8ae9;
}
.github-IssueishDetailView-tab.react-tabs__tab--selected .github-IssueishDetailView-tab-icon {
  color: #474747;
}
.github-IssueishDetailView-footer {
  padding: 10px;
  text-align: center;
  border-top: 1px solid #e0e0e0;
  background-color: #f0f0f0;
}
.github-IssueishDetailView-footerLink.icon {
  color: #949494;
}
.github-IssueishDetailView-footerLink.icon:before {
  vertical-align: -2px;
}
.github-IssueishDetailView-overview {
  max-width: 60em;
  margin: 0 auto;
}
.github-IssueishDetailView-overview > .github-DotComMarkdownHtml {
  padding: 20px;
}
.github-IssueishDetailView-reactions {
  padding: 0 20px 10px 20px;
}
.github-IssueishDetailView-reactions:empty {
  display: none;
}
.github-IssueishDetailView-reactionsGroup {
  margin-right: 20px;
}
.github-IssueishDetailView-buildStatus {
  padding: 20px;
}
.github-IssueishDetailView-buildStatus:empty {
  display: none;
}
.github-IssueishDetailView .github-PrCommitsView-commitWrapper {
  padding: 20px;
}
.github-IssueishDetailView-issueBody {
  flex: 1;
  overflow: auto;
}
.github-IssueishDetailView-issueBody > .github-DotComMarkdownHtml {
  padding: 20px;
}
