css:
.remote-ftp-view {
  position: relative;
  width: 100%;
  height: 100%;
  overflow: auto;
}
.remote-ftp-view > .offline {
  overflow: hidden;
}
.remote-ftp-view > .offline .remote-ftp-offline-inner {
  width: 100%;
}
.remote-ftp-view > .offline .remote-ftp-offline-inner > ul {
  width: 100%;
  padding: 10px;
  list-style-type: none;
}
.remote-ftp-view > .offline .remote-ftp-offline-inner > ul > li a {
  width: 100%;
  margin-bottom: 5px;
}
.remote-ftp-view .project-root.project-root > .header::before {
  line-height: 2.5em !important;
}
.remote-ftp-view .project-root.project-root > .header .name {
  line-height: 2.5em;
}
.remote-ftp-view > .queue {
  position: sticky;
  left: 0;
  right: 0;
  bottom: 0;
  display: block;
  line-height: 0;
  width: 100%;
  height: 27px;
  overflow: hidden;
}
.remote-ftp-view > .queue.active {
  position: absolute;
  height: 300px;
  z-index: 2;
  -webkit-transition: height .5s ease;
  -moz-transition: height .5s ease;
  transition: height .5s ease;
}
.remote-ftp-view > .queue > .progress {
  display: none;
  width: auto;
  height: auto;
  border-radius: 0;
  list-style: none;
  margin: 0 0 2px;
  padding: 0;
}
.remote-ftp-view > .queue > .progress > li {
  display: flex;
  margin: 0;
  padding: 4px 8px;
  font-size: 11px;
  line-height: 18px;
  flex-wrap: nowrap;
  flex-direction: row;
  justify-content: flex-start;
}
.remote-ftp-view > .queue > .progress > li progress {
  flex: none;
  width: 50px;
  margin-top: 5px;
}
.remote-ftp-view > .queue > .progress > li .name {
  width: 100%;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  text-align: left;
}
.remote-ftp-view > .queue > .progress > li .eta {
  flex: none;
  white-space: nowrap;
  text-align: right;
  margin-right: 20px;
}
.remote-ftp-view > .queue > .list {
  display: block;
  list-style: none;
  margin: 0;
  padding: 0;
}
.remote-ftp-view > .queue > .list > li {
  display: block;
  margin: 0;
  padding: 2px 8px;
  line-height: 10px;
  font-size: 10px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.remote-ftp-view > .queue > .list > li:first-of-type {
  padding-top: 4px;
  padding-bottom: 4px;
  font-size: 11px;
  line-height: 18px;
}
.remote-ftp-view > .queue > .remote-ftp-info {
  position: absolute;
  right: 0;
  top: 5px;
  cursor: pointer;
  padding-left: 4px;
  background-color: #303030;
}
.remote-ftp-view > .offline {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 3;
  opacity: 0.8;
  display: flex;
  flex-direction: column;
  -webkit-box-align: center;
  align-items: center;
  text-align: center;
  justify-content: center;
}
.remote-ftp-view > .offline .remote-ftp-picto {
  margin-bottom: 15px;
}
.remote-ftp-view > .offline .remote-ftp-picto > .icon {
  display: block;
}
.remote-ftp-view > .offline .remote-ftp-picto > .icon::before {
  font-size: 76px;
  width: 100%;
  height: 100%;
  line-height: normal;
}
.remote-ftp-view .ftptree-view.list-tree {
  font-size: 1em;
  background-color: inherit;
  isolation: isolate;
  flex-grow: 1;
  flex-shrink: 0;
  min-width: min-content;
  min-height: calc(97%);
  position: relative;
  contain: size;
  overflow: auto;
  z-index: 2;
  -webkit-user-select: none;
  display: flex;
  flex-direction: column;
}
.remote-ftp-view .ftptree-view.list-tree:focus .selected.entry {
  color: #fff;
}
.remote-ftp-view .ftptree-view.list-tree:focus .selected.entry:before {
  background-color: #5c6064;
}
.remote-ftp-view .ftptree-view.list-tree:focus .selected.entry > .header {
  color: #fff;
}
.remote-ftp-view .ftptree-view.list-tree:focus .selected.entry .project-root-header {
  background-color: #5c6064;
  color: #fff;
}
.remote-ftp-view .ftptree-view.list-tree .selected.project-root > .header {
  background-color: #4182C4;
  color: #fff;
}
.remote-ftp-view .ftptree-view.list-tree > li > div {
  padding-left: 5px;
  padding-right: 10px;
}
.remote-ftp-view .ftptree-view.list-tree .header {
  position: relative;
  pointer-events: none;
}
.remote-ftp-view .ftptree-view.list-tree .header.project-root-header {
  position: sticky;
  top: 0;
  z-index: 1;
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
  background-color: #303030;
  color: #aaa;
}
.remote-ftp-view .ftptree-view.list-tree .list-tree {
  position: static;
}
.remote-ftp-view .ftptree-view.list-tree .entry:before {
  content: '';
  position: absolute;
}
.remote-ftp-view .ftptree-view.list-tree .project-root:before {
  height: 2.5em !important;
}
.modal .remote-ftp.permission-view {
  margin-bottom: 8.5px;
}
.modal .remote-ftp.permission-view > div {
  padding: 0 10px;
}
.modal .remote-ftp.permission-view .permissions-wrapper {
  display: flex;
}
.modal .remote-ftp.permission-view .permissions-wrapper > div {
  display: flex;
  flex-direction: column;
  margin: auto;
}
.modal .remote-ftp.permission-view .permissions-wrapper > div .inline-block {
  margin: 4px 10px 4px 0;
}
.modal .remote-ftp.permission-view .permissions-wrapper > div.permission-chown {
  display: block;
  padding-top: 20px;
  height: 140px;
}
.modal .remote-ftp.permission-view .permissions-wrapper > div.permission-chown atom-text-editor {
  min-width: 140px;
}
.modal .remote-ftp.permission-view .permissions-wrapper-block {
  margin-top: 10px;
}
.RemoteFtpPopoverTooltip {
  user-select: none;
}
.RemoteFtpPopoverTooltip.tooltip {
  width: 215px;
  padding-left: 10px;
  padding-right: 10px;
  box-sizing: border-box;
  font-size: 12px !important;
}
.RemoteFtpPopoverTooltip .tooltip-inner.tooltip-inner.tooltip-inner.tooltip-inner {
  background-color: #1d1f21;
  border: 1px solid rgba(0, 0, 0, 0.5);
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  color: #aaa;
  max-height: 300px;
  white-space: normal;
  overflow-y: auto;
  padding: 0 !important;
}
.RemoteFtpPopoverTooltip.top .tooltip-arrow.tooltip-arrow {
  width: 10px;
  height: 10px;
  border-width: 0 0 1px 1px;
  border-color: rgba(0, 0, 0, 0.5);
  background-color: #1d1f21;
  border-bottom-right-radius: 2px;
  transform: rotate(-45deg);
}
.RemoteFtpPopoverTooltip .StatusBarHeader {
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
  display: flex;
  padding: 10px;
}
.RemoteFtpPopoverTooltip .StatusBarHeader div {
  flex: auto;
  display: flex;
  flex-direction: row;
  align-items: center;
}
.RemoteFtpPopoverTooltip .StatusBarInner {
  display: flex;
  padding: 10px;
}
.statusbar-view.remote-ftp {
  display: inline-block;
}
/* NOTE: Fix modal overlay */
.tooltip {
  z-index: 10000;
}
