css:
.github-IssueishDetailView {
  padding: 30px;
  overflow: auto;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  font-size: 1.166666em;
  line-height: 1.5;
  background-color: #292c2f;
}
.github-IssueishDetailView-container {
  max-width: 48em;
  margin: 0 auto;
}
.github-IssueishDetailView-header {
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 2px;
  margin-bottom: 20px;
}
.github-IssueishDetailView-headerRow {
  display: flex;
  justify-content: space-between;
  margin: 10px;
}
.github-IssueishDetailView-headerGroup {
  display: flex;
  align-items: center;
}
.github-IssueishDetailView-headerBadge {
  margin-right: 10px;
  padding: 0 0.75em;
  line-height: 2em;
}
.github-IssueishDetailView-headerLink {
  color: #555;
}
.github-IssueishDetailView-headerStatus {
  margin-left: 10px;
}
.github-IssueishDetailView-checkoutButton {
  margin-left: 10px;
}
.github-IssueishDetailView-headerRefreshButton {
  align-self: flex-start;
  color: #555;
  cursor: pointer;
}
.github-IssueishDetailView-headerRefreshButton::before {
  display: block;
  width: 16px;
  height: 16px;
  line-height: .9;
  margin-right: 0;
  text-align: center;
}
.github-IssueishDetailView-headerRefreshButton.refreshing::before {
  animation: github-IssueishDetailView-headerRefreshButtonAnimation 2s linear 30;
}
@keyframes github-IssueishDetailView-headerRefreshButtonAnimation {
  100% {
    transform: rotate(360deg);
  }
}
.github-IssueishDetailView-avatar {
  align-self: flex-start;
  margin-right: 10px;
}
.github-IssueishDetailView-avatarImage {
  width: 32px;
  height: 32px;
  border-radius: 2px;
}
.github-IssueishDetailView-title {
  display: inline-block;
  margin: 0;
  font-size: 1.5em;
  font-weight: 500;
  line-height: 1.3;
  color: #fff;
}
.github-IssueishDetailView-title:hover,
.github-IssueishDetailView-title:focus {
  text-decoration: none;
}
.github-IssueishDetailView-reactions {
  padding-top: 30px;
  border-top: 1px solid rgba(0, 0, 0, 0.5);
}
.github-IssueishDetailView-reactions:empty {
  display: none;
}
.github-IssueishDetailView-reactionsGroup {
  margin-right: 20px;
}
.github-IssueishDetailView-meta {
  font-size: .95em;
  color: #555;
}
.github-IssueishDetailView-metaAuthor {
  color: inherit;
}
.github-IssueishDetailView-tablist {
  display: flex;
  padding: 0;
  list-style: none;
}
.github-IssueishDetailView-tab {
  flex: 1;
  padding: 5px 10px;
  text-align: center;
  border: 1px solid transparent;
  border-bottom-color: rgba(0, 0, 0, 0.5);
  border-radius: 2px 2px 0 0;
  cursor: default;
}
.github-IssueishDetailView-tab.react-tabs__tab--selected {
  color: #fff;
  border-color: rgba(0, 0, 0, 0.5);
  border-bottom-color: transparent;
}
.github-IssueishDetailView-tab-icon {
  vertical-align: middle;
}
.github-IssueishDetailView-tab-icon:before {
  margin-right: .4em;
}
.github-IssueishDetailView-buildStatus {
  margin: 30px 0;
}
.github-IssueishDetailView-buildStatus:empty {
  display: none;
}
.github-IssueishDetailView-container > .github-DotComMarkdownHtml {
  margin: 30px 0;
}
.github-IssueishDetailView-footer {
  margin-top: 30px;
  text-align: center;
}
.github-IssueishDetailView-footerLink.icon {
  color: #555;
}
.github-IssueishDetailView-footerLink.icon:before {
  vertical-align: -2px;
}
atom-dock .react-tabs__tab {
  padding: none;
}
atom-dock .react-tabs__tab--selected {
  font-weight: bold;
}
atom-dock .github-IssueishDetailView {
  padding: 10px;
  font-size: 11px;
  background-color: #1d1f21;
}
atom-dock .github-IssueishDetailView-tab {
  border: none;
}
atom-dock .github-IssueishDetailView-tablist {
  border-bottom: none;
  display: inline;
}
atom-dock .github-IssueishDetailView-header {
  margin-bottom: 15px;
  padding: 0;
  border: none;
  border-radius: 0;
}
atom-dock .github-IssueishDetailView-headerGroup {
  margin: 0;
}
atom-dock .github-IssueishDetailView-avatarImage {
  width: 24px;
  height: 24px;
  border-radius: 2px;
}
atom-dock .github-IssueishDetailView-title {
  font-size: 1.25em;
  font-weight: 500;
  line-height: 1.3;
}
atom-dock .github-IssueishDetailView-reactions {
  padding-top: 10px;
}
atom-dock .github-IssueishDetailView-buildStatus {
  margin: 10px 0;
}
atom-dock .github-IssueishDetailView-buildStatus:empty {
  display: none;
}
atom-dock .github-IssueishDetailView .github-PrTimeline {
  margin-top: 10px;
}
atom-dock .github-IssueishDetailView .timeline-item {
  margin: 0;
  padding: 15px 0;
}
atom-dock .github-IssueishDetailView .pre-timeline-item-icon {
  position: relative;
  margin: 0;
}
atom-dock .github-IssueishDetailView .pre-timeline-item-icon:before {
  vertical-align: middle;
}
atom-dock .github-IssueishDetailView-container > .github-DotComMarkdownHtml {
  margin: 10px 0;
}
atom-dock .github-PrStatuses-header,
atom-dock .github-PrStatuses-list-item {
  border-radius: 0;
  padding-left: 0;
  padding-right: 0;
  border-left: none;
  border-right: none;
}
