css:
@keyframes glow {
  to {
    background: rgba(128, 128, 128, 0.4);
    box-shadow: 0 0 16px 2px rgba(128, 128, 128, 0.4);
  }
}
atom-text-editor.editor .bracket-matcher .region {
  background: rgba(128, 128, 128, 0.8);
  box-shadow: 0 0 0 2px rgba(128, 128, 128, 0.8);
  border-radius: 64px;
  padding: 4px 4px 13px 4px;
  animation: glow 1.5s steps(10, start) infinite alternate;
}
